                        <SelectItem value="assignments">Assignments</SelectItem>
                        <SelectItem value="maintenance">Maintenance</SelectItem>
                        <SelectItem value="locations">Locations</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                {/* Field Selection */}
                {selectedEntity && (
