      if (confirmation !== "DELETE_ALL_SAMPLE_DATA") {
        return res.status(400).json({ 
          error: "Invalid confirmation", 
          message: "Please provide confirmation: 'DELETE_ALL_SAMPLE_DATA'" 
        });
      }

