  // CCTV Systems routes
  app.get("/api/cctv", async (req, res) => {
    try {
      const systems = await storage.getAllCctvSystems();
      // Mask sensitive data
      const maskedSystems = systems.map(system => ({
        ...system,
        passwordHash: system.passwordHash ? "********" : null
      }));
