/* BODYCRAFT GLASSMORPHISM DESIGN SYSTEM */
:root {
  /* Glassmorphism Color Palette */
  --glass-gradient-start: #0f172a;
  --glass-gradient-mid: #1e293b;
  --glass-gradient-end: #6366f1;

  --glass-purple: #a855f7;
  --glass-pink: #ec4899;
  --glass-blue: #3b82f6;
  --glass-teal: #14b8a6;

  /* Glass Surface Colors */
  --glass-bg: rgba(255, 255, 255, 0.13);
  --glass-bg-hover: rgba(255, 255, 255, 0.18);
  --glass-border: rgba(255, 255, 255, 0.1);
  --glass-shadow: rgba(8, 7, 16, 0.6);

  /* Text Colors */
  --glass-text-primary: #e0e7ff;
  --glass-text-secondary: rgba(255, 255, 255, 0.7);
  --glass-text-muted: rgba(255, 255, 255, 0.5);

  /* Button Outline & Badges */
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  /* Scrollbar compensation for stable layout */
  --scrollbar-size: 14px;

  --background: 0 0% 98%;
  --foreground: 222 20% 15%;
  --border: 0 0% 88%;
  --card: 0 0% 100%;
  --card-foreground: 222 20% 15%;
  --card-border: 0 0% 94%;

