  --shadow-2xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.15);
  --shadow-xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.20);
  --shadow-sm: 0px 2px 0px 0px hsl(0 0% 0% / 0.20), 0px 1px 2px -1px hsl(0 0% 0% / 0.25);
  --shadow: 0px 2px 0px 0px hsl(0 0% 0% / 0.20), 0px 1px 2px -1px hsl(0 0% 0% / 0.25);
  --shadow-md: 0px 2px 0px 0px hsl(0 0% 0% / 0.25), 0px 2px 4px -1px hsl(0 0% 0% / 0.30);
  --shadow-lg: 0px 2px 0px 0px hsl(0 0% 0% / 0.25), 0px 4px 6px -1px hsl(0 0% 0% / 0.35);
  --shadow-xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.30), 0px 8px 10px -1px hsl(0 0% 0% / 0.40);
  --shadow-2xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.35);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* GLASSMORPHISM ANIMATIONS */
@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

@keyframes float-slow {
  0%, 100% { transform: translateY(0) translateX(0); }
  50% { transform: translateY(-20px) translateX(20px); }
}

@keyframes float-slower {
  0%, 100% { transform: translateY(0) translateX(0); }
  50% { transform: translateY(20px) translateX(-20px); }
}

@keyframes pulse-slow {
  0%, 100% { opacity: 0.3; transform: scale(1); }
  50% { opacity: 0.5; transform: scale(1.1); }
}

@keyframes fade-in {
  from { opacity: 0; transform: translateY(-10px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes slide-up {
  from { opacity: 0; transform: translateY(30px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  25% { transform: translateX(-5px); }
  75% { transform: translateX(5px); }
}

@layer utilities {
  /* Hide ugly search cancel button in Chrome */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* Glassmorphism Utilities */
  .glass-bg {
    background: linear-gradient(to bottom right, var(--glass-gradient-start), var(--glass-gradient-mid), var(--glass-gradient-end));
    background-size: 200% 200%;
    animation: gradient-shift 15s ease infinite;
  }

  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid var(--glass-border);
    box-shadow: 0 8px 32px 0 var(--glass-shadow),
                0 0 0 1px rgba(255, 255, 255, 0.05) inset;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 40px 0 var(--glass-shadow),
                0 0 0 1px rgba(255, 255, 255, 0.08) inset;
  }

  .glass-card-hover:hover {
    background: var(--glass-bg-hover);
  }

  .glass-input {
    background: rgba(255, 255, 255, 0.07);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    color: var(--glass-text-primary);
  }

  .glass-input:focus {
    background: rgba(255, 255, 255, 0.15) !important;
    border-color: var(--glass-purple);
  }

  .glass-gradient-text {
    background: linear-gradient(135deg, var(--glass-purple) 0%, var(--glass-pink) 50%, var(--glass-blue) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .animate-gradient-shift {
    animation: gradient-shift 15s ease infinite;
  }

  .animate-float-slow {
    animation: float-slow 8s ease-in-out infinite;
  }

  .animate-float-slower {
    animation: float-slower 10s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse-slow 6s ease-in-out infinite;
  }

  .animate-fade-in {
    animation: fade-in 0.6s ease-out;
  }

  .animate-slide-up {
    animation: slide-up 0.8s ease-out;
  }

  .animate-shake {
    animation: shake 0.4s ease-in-out;
  }

  /* Auto-fill styling */
  input:-webkit-autofill,
  input:-webkit-autofill:hover,
  input:-webkit-autofill:focus {
    -webkit-text-fill-color: var(--glass-text-primary) !important;
    -webkit-box-shadow: 0 0 0px 1000px rgba(255, 255, 255, 0.1) inset !important;
    transition: background-color 5000s ease-in-out 0s;
  }

  /* Elevation system */
  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }

  /* Reduced motion support */
  @media (prefers-reduced-motion: reduce) {
    .animate-gradient-shift,
    .animate-float-slow,
    .animate-float-slower,
    .animate-pulse-slow {
      animation: none;
    }
  }

  /* Enhanced Professional Sidebar Styling */
  [data-testid="sidebar-main"] {
    background: rgba(15, 23, 42, 0.4) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
    border-right: 1px solid rgba(255, 255, 255, 0.08) !important;
  }

  /* Sidebar menu buttons */
  [data-testid="sidebar-main"] button,
  [data-testid="sidebar-main"] a {
    color: rgba(255, 255, 255, 0.8);
    transition: all 0.2s ease;
  }

  [data-testid="sidebar-main"] button:hover,
  [data-testid="sidebar-main"] a:hover {
    background: rgba(255, 255, 255, 0.1) !important;
    color: rgba(255, 255, 255, 1);
  }

  /* Active menu item */
  [data-testid="sidebar-main"] [data-active="true"],
  [data-testid="sidebar-main"] .bg-sidebar-accent {
    background: linear-gradient(135deg, rgba(168, 85, 247, 0.2), rgba(59, 130, 246, 0.2)) !important;
    color: rgba(255, 255, 255, 1) !important;
    border-left: 3px solid var(--glass-purple);
  }

  /* Sidebar group labels */
  [data-testid="sidebar-main"] [class*="SidebarGroupLabel"] {
    color: rgba(255, 255, 255, 0.7);
    font-weight: 600;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-size: 0.75rem;
    padding-top: 1.5rem;
    padding-bottom: 0.5rem;
  }

  /* Sidebar icons */
  [data-testid="sidebar-main"] svg {
    color: rgba(168, 85, 247, 0.8);
    transition: all 0.2s ease;
  }

  [data-testid="sidebar-main"] button:hover svg,
  [data-testid="sidebar-main"] a:hover svg {
    color: var(--glass-purple);
    transform: scale(1.1);
  }

  /* Logout button special styling */
  [data-testid="button-logout"] {
    margin-top: 0.5rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding-top: 0.75rem !important;
  }

  [data-testid="button-logout"]:hover {
    background: rgba(239, 68, 68, 0.1) !important;
    color: rgba(248, 113, 113, 1) !important;
  }

  [data-testid="button-logout"]:hover svg {
    color: rgba(248, 113, 113, 1) !important;
  }

  /* Enhanced Glossy Table Styling */
  table {
    background: rgba(255, 255, 255, 0.03);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
  }

  table thead tr {
    background: rgba(168, 85, 247, 0.08);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border-bottom: 2px solid rgba(168, 85, 247, 0.2);
  }

  table tbody tr {
    background: rgba(255, 255, 255, 0.02);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    border-bottom: 1px solid rgba(255, 255, 255, 0.05);
  }

  table tbody tr:hover {
    background: rgba(168, 85, 247, 0.05);
    box-shadow: 0 4px 16px rgba(168, 85, 247, 0.1);
    transform: translateX(4px);
  }

  table tbody tr.bg-muted\/10 {
    background: rgba(168, 85, 247, 0.06) !important;
    backdrop-filter: blur(20px) !important;
    -webkit-backdrop-filter: blur(20px) !important;
  }

  /* Enhanced Card Glossy Effect */
  .rounded-lg.border {
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    box-shadow: 0 4px 24px rgba(0, 0, 0, 0.1),
                0 0 0 1px rgba(255, 255, 255, 0.05) inset;
  }

  /* Enhanced Button Glossy Effect */
  button {
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  button:hover {
    box-shadow: 0 4px 12px rgba(168, 85, 247, 0.2);
  }

  /* Enhanced Input Glossy Effect */
  input, select, textarea {
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
    background: rgba(255, 255, 255, 0.05);
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  input:focus, select:focus, textarea:focus {
    background: rgba(255, 255, 255, 0.08);
    box-shadow: 0 0 0 2px rgba(168, 85, 247, 0.3),
                0 4px 12px rgba(168, 85, 247, 0.2);
  }

  /* Enhanced Badge Glossy Effect */
  [class*="badge"] {
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  }

  /* Dialog/Modal Glossy Effect */
  [role="dialog"] {
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    background: rgba(15, 23, 42, 0.6);
    box-shadow: 0 24px 48px rgba(0, 0, 0, 0.3),
                0 0 0 1px rgba(255, 255, 255, 0.1) inset;
  }
}

/* Table Container - No Scrollbar */
.table-container-stable {
  overflow-x: auto;
  position: relative;
}

/* Hide scrollbar for WebKit browsers (Chrome, Safari, Edge) */
.table-container-stable::-webkit-scrollbar {
  display: none;
}

/* Hide scrollbar for Firefox */
.table-container-stable {
  scrollbar-width: none;
}

/* Hide scrollbar for IE/Edge */
.table-container-stable {
  -ms-overflow-style: none;
}

