  Archive,
  History
} from "lucide-react"

import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table"
import { Badge } from "@/components/ui/badge"
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { Checkbox } from "@/components/ui/checkbox"
import { Progress } from "@/components/ui/progress"
import { useToast } from "@/hooks/use-toast"
import { SidebarTrigger } from "@/components/ui/sidebar"
import { ObjectUploader } from "@/components/ObjectUploader"

interface ComplianceRecord {
  id: number
  taskName: string
  taskType: "backup" | "security_audit" | "policy_review" | "system_update" | "data_retention" | "access_review"
  description: string
  category: "data_backup" | "security" | "compliance" | "maintenance" | "governance"
  priority: "low" | "medium" | "high" | "critical"
  status: "pending" | "in_progress" | "completed" | "overdue" | "exempted"
  assignedTo: number | null
  assignedToName: string | null
  locationId: number | null
  locationName: string | null
  dueDate: string
  completionDate: string | null
  evidenceFiles: string[] | null
  complianceScore: number | null
  riskLevel: string | null
  regulatoryFramework: string | null
  notes: string | null
  createdBy: number
  createdByName: string | null
  createdAt: string
  updatedAt: string
  isOverdue: boolean
  daysUntilDue: number
}

interface Asset {
  assetId: string
  modelName: string
  brand: string
  assetType: string
  status: "available" | "assigned" | "maintenance" | "retired"
  locationId: number | null
}

interface Location {
  id: number
  outletName: string
  city: string
  state: string
}

// Status color mapping
const statusColors: Record<string, string> = {
  pending: "bg-yellow-400",
  completed: "bg-green-400",
  overdue: "bg-red-400",
  exempted: "bg-gray-500"
}

// Type color mapping
const typeColors: Record<string, string> = {
  backup: "bg-blue-400",
  security_audit: "bg-red-400",
  policy_review: "bg-purple-400",
  system_update: "bg-orange-500",
  data_retention: "bg-cyan-500",
  access_review: "bg-pink-500"
}

// Category icons
const categoryIcons: Record<string, any> = {
  data_backup: HardDrive,
  security: Shield,
  compliance: FileText,
  maintenance: Settings,
  governance: Building2
}

export default function CompliancePage() {
  const [searchTerm, setSearchTerm] = useState("")
  const [statusFilter, setStatusFilter] = useState<string>("all")
  const [typeFilter, setTypeFilter] = useState<string>("all")
  const [categoryFilter, setCategoryFilter] = useState<string>("all")
  const [locationFilter, setLocationFilter] = useState<string>("all")
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false)
  const [selectedRecord, setSelectedRecord] = useState<ComplianceRecord | null>(null)
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false)
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false)
  const [isCompleteDialogOpen, setIsCompleteDialogOpen] = useState(false)

  const { toast } = useToast()
  const queryClient = useQueryClient()

  // Fetch data from API
  const { data: assets } = useQuery<Asset[]>({
    queryKey: ["/api/assets"],
  })

  const { data: locations } = useQuery<Location[]>({
    queryKey: ["/api/locations"],
  })

  const { data: users } = useQuery<any[]>({
    queryKey: ["/api/users"],
  })

  // Fetch compliance tasks from real API
  const { data: compliance, isLoading: isLoadingCompliance } = useQuery<any[]>({
    queryKey: ["/api/compliance/tasks"],
  })

  // Create compliance task mutation
  const createTaskMutation = useMutation({
    mutationFn: async (taskData: any) => {
      const response = await fetch('/api/compliance/tasks', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(taskData),
        credentials: 'include'
      })
      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Failed to create task')
      }
      return response.json()
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/compliance/tasks'] })
      toast({ title: "Success", description: "Compliance task created successfully" })
      setIsCreateDialogOpen(false)
    },
    onError: (error: Error) => {
      toast({ 
        title: "Error", 
        description: error.message || "Failed to create task", 
        variant: "destructive" 
      })
    }
  })

  // Update compliance task mutation
  const updateTaskMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number, data: any }) => {
      const response = await fetch(`/api/compliance/tasks/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include'
      })
      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Failed to update task')
      }
      return response.json()
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/compliance/tasks'] })
      queryClient.invalidateQueries({ queryKey: ['/api/compliance/tasks', variables.id, 'audit-trail'] })
      toast({ title: "Success", description: "Task updated successfully" })
      setIsEditDialogOpen(false)
      setIsCompleteDialogOpen(false)
    },
    onError: (error: Error) => {
      toast({ 
        title: "Error", 
        description: error.message || "Failed to update task", 
        variant: "destructive" 
      })
    }
  })

  // Delete compliance task mutation
  const deleteTaskMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/compliance/tasks/${id}`, {
        method: 'DELETE',
        credentials: 'include'
      })
      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Failed to delete task')
      }
      return response.json()
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/compliance/tasks'] })
      toast({ title: "Success", description: "Task deleted successfully" })
    },
    onError: (error: Error) => {
      toast({ 
        title: "Error", 
        description: error.message || "Failed to delete task", 
        variant: "destructive" 
      })
    }
  })

  // Evidence upload mutation
  const uploadEvidenceMutation = useMutation({
    mutationFn: async ({ taskId, fileName, fileUrl, fileType, fileSize }: { taskId: number, fileName: string, fileUrl: string, fileType: string, fileSize: number }) => {
      const response = await fetch(`/api/compliance/tasks/${taskId}/evidence`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ fileName, fileUrl, fileType, fileSize }),
        credentials: 'include'
      })
      if (!response.ok) {
        const error = await response.json()
        throw new Error(error.error || 'Failed to upload evidence')
      }
      return response.json()
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/compliance/tasks'] })
      queryClient.invalidateQueries({ queryKey: ['/api/compliance/tasks', variables.taskId, 'audit-trail'] })
      toast({ title: "Success", description: "Evidence uploaded successfully" })
    },
    onError: (error: Error) => {
      toast({ 
        title: "Error", 
        description: error.message || "Failed to upload evidence", 
        variant: "destructive" 
      })
    }
  })

  // Fetch audit trail for selected task
  const { data: auditTrail } = useQuery<any[]>({
    queryKey: ['/api/compliance/tasks', selectedRecord?.id, 'audit-trail'],
    enabled: !!selectedRecord?.id && isViewDialogOpen,
  })

  // Function to get upload parameters for object storage
  const getUploadParameters = async () => {
    const response = await fetch('/api/object-storage/upload-url', {
      method: 'POST',
      credentials: 'include'
    })
    if (!response.ok) {
      throw new Error('Failed to get upload URL')
    }
    const data = await response.json()
    return {
      method: 'PUT' as const,
      url: data.url
    }
  }

  // Helper function to get compliance status
  const getComplianceStatus = (record: ComplianceRecord) => {
    if (record.isOverdue) return "overdue"
    return record.status
  }

  // Filter compliance records
  const filteredCompliance = compliance?.filter(record => {
    const status = record.isOverdue ? 'overdue' : record.status

    const matchesSearch = 
      record.taskName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      record.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      record.assignedToName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      record.locationName?.toLowerCase().includes(searchTerm.toLowerCase())

    const matchesStatus = statusFilter === "all" || status === statusFilter
    const matchesType = typeFilter === "all" || record.taskType === typeFilter
    const matchesCategory = categoryFilter === "all" || record.category === categoryFilter
    const matchesLocation = locationFilter === "all" || record.locationId?.toString() === locationFilter

    return matchesSearch && matchesStatus && matchesType && matchesCategory && matchesLocation
  }) || []

  // Helper functions
  const getAssetInfo = (assetId: string | null) => {
    if (!assetId) return null
    return assets?.find(asset => asset.assetId === assetId)
  }

  const getLocationName = (locationId: number | null) => {
    if (!locationId) return "All Locations"
    const location = locations?.find(loc => loc.id === locationId)
    return location ? `${location.outletName}, ${location.city}` : "Unknown"
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    })
  }

  const getStatusBadgeColor = (status: string) => {
    switch (status) {
      case "completed": return "bg-green-100 text-green-800"
      case "pending": return "bg-yellow-100 text-yellow-800" 
      case "overdue": return "bg-red-100 text-red-800"
      case "exempted": return "bg-gray-100 text-gray-800"
      default: return "bg-gray-100 text-gray-800"
    }
  }

  const getTypeIcon = (type: string) => {
    switch (type) {
      case "backup": return HardDrive
      case "security_audit": return Shield
      case "policy_review": return FileText
      case "system_update": return Monitor
      case "data_retention": return Archive
      case "access_review": return Key
      default: return Settings
    }
  }

  // Calculate compliance metrics
  const totalRecords = compliance?.length || 0
  const completedRecords = compliance?.filter(r => r.status === 'completed').length || 0
  const pendingRecords = compliance?.filter(r => r.status === 'pending').length || 0
  const overdueRecords = compliance?.filter(r => r.isOverdue).length || 0
  const complianceRate = totalRecords > 0 ? Math.round((completedRecords / totalRecords) * 100) : 0

  return (
    <div className="p-6 space-y-6 animate-fade-in">
      <SidebarTrigger data-testid="button-sidebar-toggle" className="mb-4 text-white/80 hover:text-white hover:bg-white/10 rounded-md" />

      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-white">Backup & Compliance</h1>
          <p className="text-white/70">
            Monitor compliance activities and backup verification across all locations
          </p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" className="gap-2">
            <Upload className="h-4 w-4" />
            Import Tasks
          </Button>
          <Button variant="outline" className="gap-2">
            <Download className="h-4 w-4" />
            Export Report
          </Button>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="gap-2">
                <Plus className="h-4 w-4" />
                Add Compliance Task
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create Compliance Task</DialogTitle>
                <DialogDescription>
                  Add a new compliance monitoring task or backup verification
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={(e) => {
                e.preventDefault()
                const formData = new FormData(e.currentTarget)
                const data = {
                  taskName: formData.get('taskName') as string,
                  taskType: formData.get('taskType') as string,
                  category: formData.get('category') as string,
                  description: formData.get('description') as string,
                  priority: formData.get('priority') as string,
                  locationId: parseInt(formData.get('locationId') as string),
                  dueDate: formData.get('dueDate') as string,
                  assignedTo: formData.get('assignedTo') ? parseInt(formData.get('assignedTo') as string) : null,
                }
                createTaskMutation.mutate(data)
              }} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="taskType">Task Type *</Label>
                    <Select name="taskType" required>
                      <SelectTrigger>
                        <SelectValue placeholder="Select task type" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="backup">Backup Verification</SelectItem>
                        <SelectItem value="security_audit">Security Audit</SelectItem>
                        <SelectItem value="policy_review">Policy Review</SelectItem>
                        <SelectItem value="system_update">System Update</SelectItem>
                        <SelectItem value="data_retention">Data Retention</SelectItem>
                        <SelectItem value="access_review">Access Review</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="category">Category *</Label>
                    <Select name="category" required>
                      <SelectTrigger>
                        <SelectValue placeholder="Select category" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="data_backup">Data Backup</SelectItem>
                        <SelectItem value="security">Security</SelectItem>
                        <SelectItem value="compliance">Compliance</SelectItem>
                        <SelectItem value="maintenance">Maintenance</SelectItem>
                        <SelectItem value="governance">Governance</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="taskName">Task Title *</Label>
                  <Input
                    id="taskName"
                    name="taskName"
                    placeholder="Weekly backup verification"
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="description">Description *</Label>
                  <Textarea
                    id="description"
                    name="description"
                    placeholder="Detailed description of the compliance task"
                    rows={3}
                    required
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="priority">Priority *</Label>
                    <Select name="priority" required>
                      <SelectTrigger>
                        <SelectValue placeholder="Select priority" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="low">Low</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="high">High</SelectItem>
                        <SelectItem value="critical">Critical</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="locationId">Location *</Label>
                    <Select name="locationId" required>
                      <SelectTrigger>
                        <SelectValue placeholder="Select location" />
                      </SelectTrigger>
                      <SelectContent>
                        {locations?.map(location => (
                          <SelectItem key={location.id} value={location.id.toString()}>
                            {location.outletName}, {location.city}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="dueDate">Due Date *</Label>
                    <Input
                      id="dueDate"
                      name="dueDate"
                      type="date"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="assignedTo">Assigned To</Label>
                    <Select name="assignedTo">
                      <SelectTrigger>
                        <SelectValue placeholder="Select user (optional)" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="unassigned">Unassigned</SelectItem>
                        {users?.map(user => (
                          <SelectItem key={user.id} value={user.id.toString()}>
                            {user.fullName} ({user.role})
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <DialogFooter>
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setIsCreateDialogOpen(false)}
                    disabled={createTaskMutation.isPending}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createTaskMutation.isPending}>
                    {createTaskMutation.isPending ? "Creating..." : "Create Compliance Task"}
                  </Button>
                </DialogFooter>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Tasks</CardTitle>
            <FileText className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">{totalRecords}</div>
            <p className="text-xs text-muted-foreground">
              All compliance tasks
            </p>
          </CardContent>
        </Card>

        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Completed</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">{completedRecords}</div>
            <p className="text-xs text-muted-foreground">
              Tasks completed
            </p>
          </CardContent>
        </Card>

        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Overdue</CardTitle>
            <AlertTriangle className="h-4 w-4 text-red-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">{overdueRecords}</div>
            <p className="text-xs text-muted-foreground">
              Past due date
            </p>
          </CardContent>
        </Card>

        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Compliance Rate</CardTitle>
            <Shield className="h-4 w-4 text-purple-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">{complianceRate}%</div>
            <div className="mt-2">
              <Progress value={complianceRate} className="h-2" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card className="glass-card border-0 glass-card border-0">
        <CardHeader>
          <CardTitle className="text-lg">Search & Filter Compliance</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search by Title, Description, Asset ID, Assigned Person..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-8"
                />
              </div>
            </div>
            <div className="flex gap-2">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-[140px]">
                  <SelectValue placeholder="Status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="overdue">Overdue</SelectItem>
                  <SelectItem value="exempted">Exempted</SelectItem>
                </SelectContent>
              </Select>

              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-[140px]">
                  <SelectValue placeholder="Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="backup">Backup</SelectItem>
                  <SelectItem value="security_audit">Security Audit</SelectItem>
                  <SelectItem value="policy_review">Policy Review</SelectItem>
                  <SelectItem value="system_update">System Update</SelectItem>
                  <SelectItem value="data_retention">Data Retention</SelectItem>
                  <SelectItem value="access_review">Access Review</SelectItem>
                </SelectContent>
              </Select>

              <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                <SelectTrigger className="w-[140px]">
                  <SelectValue placeholder="Category" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  <SelectItem value="data_backup">Data Backup</SelectItem>
                  <SelectItem value="security">Security</SelectItem>
                  <SelectItem value="compliance">Compliance</SelectItem>
                  <SelectItem value="maintenance">Maintenance</SelectItem>
                  <SelectItem value="governance">Governance</SelectItem>
                </SelectContent>
              </Select>

              <Select value={locationFilter} onValueChange={setLocationFilter}>
                <SelectTrigger className="w-[180px]">
                  <SelectValue placeholder="Location" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Locations</SelectItem>
                  {locations?.map(location => (
                    <SelectItem key={location.id} value={location.id.toString()}>
                      {location.outletName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="text-sm text-muted-foreground">
            Showing {filteredCompliance.length} of {compliance?.length || 0} compliance records
          </div>
        </CardContent>
      </Card>

      {/* Compliance Table */}
      <Card className="glass-card border-0 glass-card border-0">
        <CardContent className="p-0">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Task</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Asset/Location</TableHead>
                <TableHead>Due Date</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Assigned To</TableHead>
                <TableHead>Evidence</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredCompliance.map((record) => {
                const status = getComplianceStatus(record)
                const TypeIcon = getTypeIcon(record.taskType)

                return (
                  <TableRow key={record.id}>
                    <TableCell>
                      <div className="flex items-center gap-3">
                        <TypeIcon className="h-4 w-4 text-muted-foreground" />
                        <div>
                          <div className="font-medium">{record.taskName}</div>
                          <div className="text-sm text-muted-foreground truncate max-w-xs">
                            {record.description}
                          </div>
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <div 
                          className={`w-2 h-2 rounded-full ${typeColors[record.taskType]}`}
                        />
                        <span className="capitalize">{record.taskType.replace('_', ' ')}</span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm text-muted-foreground">
                        {record.locationName || "All Locations"}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-1">
                        <Calendar className="h-3 w-3 text-muted-foreground" />
                        {formatDate(record.dueDate)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge 
                        variant="secondary" 
                        className={getStatusBadgeColor(status)}
                      >
                        {status}
                      </Badge>
                      {record.completionDate && (
                        <div className="text-xs text-muted-foreground mt-1">
                          Completed: {formatDate(record.completionDate)}
                        </div>
                      )}
                    </TableCell>
                    <TableCell>
                      {record.assignedToName || "Unassigned"}
                    </TableCell>
                    <TableCell>
                      {record.evidenceFiles && record.evidenceFiles.length > 0 ? (
                        <div className="flex items-center gap-1 text-green-600">
                          <CheckCircle className="h-3 w-3" />
                          <span className="text-xs">{record.evidenceFiles.length} file(s)</span>
                        </div>
                      ) : (
                        <div className="flex items-center gap-1 text-gray-400">
                          <XCircle className="h-3 w-3" />
                          <span className="text-xs">No evidence</span>
                        </div>
                      )}
                    </TableCell>
                    <TableCell>
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button variant="ghost" className="h-8 w-8 p-0">
                            <MoreHorizontal className="h-4 w-4" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                          <DropdownMenuLabel>Actions</DropdownMenuLabel>
                          <DropdownMenuItem
                            onClick={() => {
                              setSelectedRecord(record)
                              setIsViewDialogOpen(true)
                            }}
                          >
                            <Eye className="mr-2 h-4 w-4" />
                            View Details
                          </DropdownMenuItem>
                          <DropdownMenuItem
                            onClick={() => {
                              setSelectedRecord(record)
                              setIsEditDialogOpen(true)
                            }}
                          >
                            <Edit className="mr-2 h-4 w-4" />
                            Edit Task
                          </DropdownMenuItem>
                          {!record.completionDate && (
                            <DropdownMenuItem
                              onClick={() => {
                                setSelectedRecord(record)
                                setIsCompleteDialogOpen(true)
                              }}
                            >
                              <CheckCircle className="mr-2 h-4 w-4" />
                              Mark Complete
                            </DropdownMenuItem>
                          )}
                          <DropdownMenuSeparator />
                          <DropdownMenuItem 
                            className="text-red-600"
                            onClick={() => deleteTaskMutation.mutate(record.id)}
                          >
                            <Trash2 className="mr-2 h-4 w-4" />
                            Delete Task
                          </DropdownMenuItem>
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </TableCell>
                  </TableRow>
                )
              })}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* View Record Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Compliance Task Details</DialogTitle>
            <DialogDescription>
              Complete information about this compliance task
            </DialogDescription>
          </DialogHeader>
          {selectedRecord && (
            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Task Type</Label>
                  <div className="text-sm p-2 bg-muted rounded capitalize">
                    {selectedRecord.taskType.replace('_', ' ')}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Priority</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    <Badge variant={selectedRecord.priority === 'critical' ? 'destructive' : 'secondary'}>
                      {selectedRecord.priority}
                    </Badge>
                  </div>
                </div>
              </div>

              <div className="space-y-2">
                <Label className="text-sm font-medium">Task Name</Label>
                <div className="text-sm p-2 bg-muted rounded font-medium">
                  {selectedRecord.taskName}
                </div>
              </div>

              <div className="space-y-2">
                <Label className="text-sm font-medium">Description</Label>
                <div className="text-sm p-2 bg-muted rounded">
                  {selectedRecord.description}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Due Date</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {formatDate(selectedRecord.dueDate)}
                    {selectedRecord.daysUntilDue !== undefined && (
                      <div className="text-xs text-muted-foreground mt-1">
                        {selectedRecord.daysUntilDue >= 0 ? `${selectedRecord.daysUntilDue} days remaining` : `${Math.abs(selectedRecord.daysUntilDue)} days overdue`}
                      </div>
                    )}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Status</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    <Badge className={getStatusBadgeColor(getComplianceStatus(selectedRecord))}>
                      {getComplianceStatus(selectedRecord)}
                    </Badge>
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Assigned To</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedRecord.assignedToName || "Unassigned"}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Location</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedRecord.locationName || "N/A"}
                  </div>
                </div>
              </div>

              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <Label className="text-sm font-medium">Evidence Files</Label>
                  <ObjectUploader
                    maxNumberOfFiles={5}
                    maxFileSize={10485760}
                    onGetUploadParameters={getUploadParameters}
                    onComplete={(result) => {
                      const file = result.successful[0];
                      if (file && file.uploadURL) {
                        uploadEvidenceMutation.mutate({
                          taskId: selectedRecord.id,
                          fileName: file.name,
                          fileUrl: file.uploadURL.split('?')[0],
                          fileType: file.type || 'application/octet-stream',
                          fileSize: file.size
                        });
                      }
                    }}
                    buttonClassName="gap-2 h-8 text-xs"
                  >
                    <Upload className="h-3 w-3" />
                    Upload Evidence
                  </ObjectUploader>
                </div>
                <div className="border rounded-md p-3 bg-muted/50">
                  {selectedRecord.evidenceFiles && selectedRecord.evidenceFiles.length > 0 ? (
                    <div className="space-y-2">
                      {selectedRecord.evidenceFiles.map((file, index) => (
                        <div key={index} className="flex items-center justify-between p-2 bg-background rounded border">
                          <div className="flex items-center gap-2">
                            <FileText className="h-4 w-4 text-muted-foreground" />
                            <span className="text-sm">{file}</span>
                          </div>
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => window.open(file, '_blank')}
                            data-testid={`button-download-evidence-${index}`}
                          >
                            <Download className="h-3 w-3" />
                          </Button>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-sm text-muted-foreground text-center py-2">
                      No evidence files uploaded
                    </div>
                  )}
                </div>
              </div>

              {selectedRecord.notes && (
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Notes</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedRecord.notes}
                  </div>
                </div>
              )}

              <div className="space-y-2 border-t pt-4">
                <Label className="text-sm font-medium flex items-center gap-2">
                  <History className="h-4 w-4" />
                  Audit Trail
                </Label>
                <div className="border rounded-md p-3 bg-muted/50 max-h-60 overflow-y-auto">
                  {auditTrail && auditTrail.length > 0 ? (
                    <div className="space-y-3">
                      {auditTrail.map((entry, index) => (
                        <div key={index} className="text-sm border-l-2 border-primary/30 pl-3 pb-2" data-testid={`audit-trail-entry-${index}`}>
                          <div className="flex items-center justify-between mb-1">
                            <span className="font-medium capitalize text-primary">
                              {entry.action.replace('_', ' ')}
                            </span>
                            <span className="text-xs text-muted-foreground">
                              {new Date(entry.timestamp).toLocaleString('en-IN', { 
                                day: '2-digit',
                                month: 'short', 
                                year: 'numeric',
                                hour: '2-digit',
                                minute: '2-digit',
                                hour12: true
                              })}
                            </span>
                          </div>
                          <div className="text-xs text-muted-foreground">
                            by {entry.performedByName}
                          </div>
                          {entry.ipAddress && (
                            <div className="text-xs text-muted-foreground mt-1">
                              IP: {entry.ipAddress}
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-sm text-muted-foreground text-center py-2">
                      No audit trail available
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsViewDialogOpen(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

client/src/pages/employees.tsx.content:
import { useState, useEffect } from "react"
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query"
import { 
  Users, 
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Eye, 
  Download,
  Upload,
  MoreHorizontal,
  User,
  MapPin,
  Briefcase,
  Phone,
  Mail,
  Calendar,
  Badge as BadgeIcon,
  Building2,
  Laptop,
  ChevronDown,
  ChevronUp,
  X,
  SlidersHorizontal,
  Filter
} from "lucide-react"

import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table"
import { Badge } from "@/components/ui/badge"
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import { Label } from "@/components/ui/label"
import { useToast } from "@/hooks/use-toast"
import { SidebarTrigger } from "@/components/ui/sidebar"
import { ExcelExporter, ExcelImporter } from "@/lib/excel"

// Status color mapping
const statusColors: Record<string, string> = {
  active: "bg-green-400",
  inactive: "bg-red-400",
  on_leave: "bg-yellow-400",
}

interface Employee {
  id: number
  employeeCode: string
  firstName: string
  lastName: string
  department: string
  designation: string
  email: string
  phone: string
  status: "active" | "inactive" | "on_leave"
  locationId: number | null
  createdAt: string
  updatedAt: string
}

interface Location {
  id: number
  outletName: string
  city: string
  state: string
}

interface Asset {
  assetId: string
  modelName: string
  brand: string
  assetType: string
  currentUserId: number | null
}

interface Assignment {
  assetId: string
  employeeId: number
  assignedDate: string
  returnedDate: string | null
}

export default function EmployeesPage() {
  const [searchTerm, setSearchTerm] = useState("")
  const [statusFilter, setStatusFilter] = useState<string>("all")
  const [departmentFilter, setDepartmentFilter] = useState<string>("all")
  const [locationFilter, setLocationFilter] = useState<string>("all")
  const [designationFilter, setDesignationFilter] = useState<string>("all")
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false)
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false)
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null)
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false)
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false)
  const [expandedEmployeeId, setExpandedEmployeeId] = useState<number | null>(null)
  const [isImportDialogOpen, setIsImportDialogOpen] = useState(false)
  const [importFile, setImportFile] = useState<File | null>(null)
  const [isImporting, setIsImporting] = useState(false)

  // Form state for create dialog
  const [createEmployee, setCreateEmployee] = useState({
    employeeCode: '',
    firstName: '',
    lastName: '',
    department: '',
    designation: '',
    email: '',
    phone: '',
    status: 'active',
    locationId: ''
  })

  // Form state for edit dialog
  const [editEmployee, setEditEmployee] = useState({
    firstName: '',
    lastName: '',
    department: '',
    designation: '',
    email: '',
    phone: '',
    status: 'active',
    locationId: ''
  })

  const { toast } = useToast()
  const queryClient = useQueryClient()

  // Reset create form when dialog opens
  useEffect(() => {
    if (isCreateDialogOpen) {
      setCreateEmployee({
        employeeCode: '',
        firstName: '',
        lastName: '',
        department: '',
        designation: '',
        email: '',
        phone: '',
        status: 'active',
        locationId: ''
      })
    }
  }, [isCreateDialogOpen])

  // Populate editEmployee when selectedEmployee changes
  useEffect(() => {
    if (selectedEmployee && isEditDialogOpen) {
      setEditEmployee({
        firstName: selectedEmployee.firstName,
        lastName: selectedEmployee.lastName,
        department: selectedEmployee.department,
        designation: selectedEmployee.designation,
        email: selectedEmployee.email || '',
        phone: selectedEmployee.phone || '',
        status: selectedEmployee.status,
        locationId: selectedEmployee.locationId?.toString() || ''
      })
    }
  }, [selectedEmployee, isEditDialogOpen])

  // Fetch data
  const { data: employees, isLoading: employeesLoading } = useQuery<Employee[]>({
    queryKey: ["/api/employees"],
  })

  const { data: locations } = useQuery<Location[]>({
    queryKey: ["/api/locations"],
  })

  const { data: assets } = useQuery<Asset[]>({
    queryKey: ["/api/assets"],
  })

  const { data: assignments } = useQuery<Assignment[]>({
    queryKey: ["/api/assignments"],
  })

  // Get unique departments and designations for filters
  const departments = Array.from(new Set(employees?.map(emp => emp.department) || []))
  const designations = Array.from(new Set(employees?.map(emp => emp.designation) || []))

  // Filter employees
  const filteredEmployees = employees?.filter(employee => {
    const matchesSearch = 
      employee.employeeCode.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.department.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.designation.toLowerCase().includes(searchTerm.toLowerCase())

    const matchesStatus = statusFilter === "all" || employee.status === statusFilter
    const matchesDepartment = departmentFilter === "all" || employee.department === departmentFilter
    const matchesLocation = locationFilter === "all" || employee.locationId?.toString() === locationFilter
    const matchesDesignation = designationFilter === "all" || employee.designation === designationFilter

    return matchesSearch && matchesStatus && matchesDepartment && matchesLocation && matchesDesignation
  }) || []

  // Count active filters
  const activeFiltersCount = [
    departmentFilter !== "all",
    locationFilter !== "all",
    designationFilter !== "all"
  ].filter(Boolean).length

  // Create employee mutation
  const createEmployeeMutation = useMutation({
    mutationFn: async (employeeData: any) => {
      const response = await fetch('/api/employees', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(employeeData),
      })
      if (!response.ok) throw new Error('Failed to create employee')
      return response.json()
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] })
      toast({ title: "Success", description: "Employee created successfully" })
      setIsCreateDialogOpen(false)
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create employee", variant: "destructive" })
    }
  })

  // Update employee mutation  
  const updateEmployeeMutation = useMutation({
    mutationFn: async ({ employeeId, data }: { employeeId: number, data: any }) => {
      const response = await fetch(`/api/employees/${employeeId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      })
      if (!response.ok) throw new Error('Failed to update employee')
      return response.json()
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] })
      toast({ title: "Success", description: "Employee updated successfully" })
      setIsEditDialogOpen(false)
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update employee", variant: "destructive" })
    }
  })

  // Delete employee mutation
  const deleteEmployeeMutation = useMutation({
    mutationFn: async (employeeId: number) => {
      const response = await fetch(`/api/employees/${employeeId}`, {
        method: 'DELETE',
      })
      if (!response.ok) throw new Error('Failed to delete employee')
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/employees'] })
      toast({ title: "Success", description: "Employee deleted successfully" })
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete employee", variant: "destructive" })
    }
  })

  // Excel Export Handler
  const handleExport = () => {
    if (!employees || !locations || !assignments || !assets) {
      toast({ title: "Error", description: "Data not loaded yet", variant: "destructive" })
      return
    }

    try {
      ExcelExporter.exportEmployees(employees, locations, assignments, assets)
      toast({ title: "Success", description: "Employees exported successfully" })
    } catch (error) {
      toast({ title: "Error", description: "Failed to export employees", variant: "destructive" })
    }
  }

  // Excel Import Handler
  const handleImport = async () => {
    if (!importFile) {
      toast({ title: "Error", description: "Please select a file", variant: "destructive" })
      return
    }

    setIsImporting(true)

    try {
      const data = await ExcelImporter.parseExcelFile(importFile)
      const { valid, errors } = ExcelImporter.validateEmployeeData(data)

      if (errors.length > 0) {
        toast({ 
          title: "Validation Errors", 
          description: `${errors.length} errors found. First error: ${errors[0]}`,
          variant: "destructive" 
        })
        setIsImporting(false)
        return
      }

      for (const employeeData of valid) {
        await createEmployeeMutation.mutateAsync(employeeData)
      }

      toast({ 
        title: "Success", 
        description: `Successfully imported ${valid.length} employees` 
      })
      setIsImportDialogOpen(false)
      setImportFile(null)
    } catch (error) {
      toast({ 
        title: "Error", 
        description: "Failed to import employees", 
        variant: "destructive" 
      })
    } finally {
      setIsImporting(false)
    }
  }

  // Helper functions
  const getLocationName = (locationId: number | null) => {
    if (!locationId) return "No location assigned"
    const location = locations?.find(loc => loc.id === locationId)
    return location ? `${location.outletName}, ${location.city}` : "Unknown location"
  }

  const getEmployeeAssets = (employeeId: number) => {
    const activeAssignments = assignments?.filter(
      assignment => assignment.employeeId === employeeId && !assignment.returnedDate
    ) || []

    return activeAssignments.map(assignment => {
      const asset = assets?.find(a => a.assetId === assignment.assetId)
      return asset
    }).filter(Boolean)
  }

  const handleCreateEmployee = (event: React.FormEvent) => {
    event.preventDefault()

    // Validate required fields
    if (!createEmployee.employeeCode?.trim() || !createEmployee.firstName?.trim() || !createEmployee.lastName?.trim() || !createEmployee.department || !createEmployee.designation?.trim()) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields (Employee Code, First Name, Last Name, Department, Designation)",
        variant: "destructive",
      })
      return
    }

    const employeeData = {
      employeeCode: createEmployee.employeeCode.trim(),
      firstName: createEmployee.firstName.trim(),
      lastName: createEmployee.lastName.trim(),
      department: createEmployee.department,
      designation: createEmployee.designation.trim(),
      email: createEmployee.email?.trim() || null,
      phone: createEmployee.phone?.trim() || null,
      status: createEmployee.status,
      locationId: createEmployee.locationId ? parseInt(createEmployee.locationId) : null,
    }

    createEmployeeMutation.mutate(employeeData)
  }

  const handleUpdateEmployee = (event: React.FormEvent) => {
    event.preventDefault()
    if (!selectedEmployee) return

    // Validate required fields
    if (!editEmployee.firstName?.trim() || !editEmployee.lastName?.trim() || !editEmployee.department?.trim() || !editEmployee.designation?.trim()) {
      toast({ 
        title: "Validation Error", 
        description: "Please fill in all required fields (First Name, Last Name, Department, Designation)", 
        variant: "destructive" 
      })
      return
    }

    const employeeData = {
      firstName: editEmployee.firstName.trim(),
      lastName: editEmployee.lastName.trim(),
      department: editEmployee.department,
      designation: editEmployee.designation.trim(),
      email: editEmployee.email?.trim() || null,
      phone: editEmployee.phone?.trim() || null,
      status: editEmployee.status,
      locationId: editEmployee.locationId ? parseInt(editEmployee.locationId) : null,
    }

    updateEmployeeMutation.mutate({ employeeId: selectedEmployee.id, data: employeeData })
  }

  if (employeesLoading) {
    return (
      <div className="p-6 space-y-6 animate-fade-in">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-1/4 mb-6"></div>
          <div className="space-y-4">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="h-16 bg-muted rounded"></div>
            ))}
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="p-6 space-y-6 animate-fade-in">
      <SidebarTrigger data-testid="button-sidebar-toggle" className="mb-4 text-white/80 hover:text-white hover:bg-white/10 rounded-md" />

      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-white">Employees</h1>
          <p className="text-white/70">
            Human resource management and organizational structure oversight
          </p>
        </div>
        <div className="flex gap-2">
          <Dialog open={isImportDialogOpen} onOpenChange={setIsImportDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline" className="gap-2" data-testid="button-import-excel">
                <Upload className="h-4 w-4" />
                Import Excel
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Import Employees from Excel</DialogTitle>
                <DialogDescription>
                  Upload an Excel file to import employees. Download the template for the correct format.
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="import-file">Excel File</Label>
                  <Input
                    id="import-file"
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={(e) => setImportFile(e.target.files?.[0] || null)}
                    data-testid="input-import-file"
                  />
                </div>
                <Button
                  variant="outline"
                  onClick={() => ExcelImporter.downloadEmployeeTemplate()}
                  data-testid="button-download-template"
                >
                  Download Template
                </Button>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => setIsImportDialogOpen(false)} data-testid="button-cancel-import">
                  Cancel
                </Button>
                <Button onClick={handleImport} disabled={!importFile || isImporting} data-testid="button-confirm-import">
                  {isImporting ? "Importing..." : "Import"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
          <Button variant="outline" className="gap-2" onClick={handleExport} data-testid="button-export">
            <Download className="h-4 w-4" />
            Export
          </Button>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="gap-2">
                <Plus className="h-4 w-4" />
                Add Employee
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New Employee</DialogTitle>
                <DialogDescription>
                  Onboard new personnel with complete organizational details and department assignment
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={handleCreateEmployee} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="employeeCode">Employee Code *</Label>
                  <Input
                    id="employeeCode"
                    value={createEmployee.employeeCode}
                    onChange={(e) => setCreateEmployee({ ...createEmployee, employeeCode: e.target.value })}
                    placeholder="BFC2024001"
                    required
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="firstName">First Name *</Label>
                    <Input
                      id="firstName"
                      value={createEmployee.firstName}
                      onChange={(e) => setCreateEmployee({ ...createEmployee, firstName: e.target.value })}
                      placeholder="John"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="lastName">Last Name *</Label>
                    <Input
                      id="lastName"
                      value={createEmployee.lastName}
                      onChange={(e) => setCreateEmployee({ ...createEmployee, lastName: e.target.value })}
                      placeholder="Doe"
                      required
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="department">Department *</Label>
                    <Select value={createEmployee.department} onValueChange={(value) => setCreateEmployee({ ...createEmployee, department: value })} required>
                      <SelectTrigger>
                        <SelectValue placeholder="Select department" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="IT">IT</SelectItem>
                        <SelectItem value="Sales">Sales</SelectItem>
                        <SelectItem value="Marketing">Marketing</SelectItem>
                        <SelectItem value="Operations">Operations</SelectItem>
                        <SelectItem value="Finance">Finance</SelectItem>
                        <SelectItem value="HR">HR</SelectItem>
                        <SelectItem value="Management">Management</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="designation">Designation *</Label>
                    <Input
                      id="designation"
                      value={createEmployee.designation}
                      onChange={(e) => setCreateEmployee({ ...createEmployee, designation: e.target.value })}
                      placeholder="Software Engineer, Sales Manager..."
                      required
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="email">Email Address *</Label>
                    <Input
                      id="email"
                      type="email"
                      value={createEmployee.email}
                      onChange={(e) => setCreateEmployee({ ...createEmployee, email: e.target.value })}
                      placeholder="john.doe@bodycraft.com"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number *</Label>
                    <Input
                      id="phone"
                      value={createEmployee.phone}
                      onChange={(e) => setCreateEmployee({ ...createEmployee, phone: e.target.value })}
                      placeholder="+91 9876543210"
                      required
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="locationId">Location</Label>
                    <Select value={createEmployee.locationId} onValueChange={(value) => setCreateEmployee({ ...createEmployee, locationId: value })}>
                      <SelectTrigger>
                        <SelectValue placeholder="Select location" />
                      </SelectTrigger>
                      <SelectContent>
                        {locations?.map(location => (
                          <SelectItem key={location.id} value={location.id.toString()}>
                            {location.outletName}, {location.city}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="status">Status</Label>
                    <Select value={createEmployee.status} onValueChange={(value: any) => setCreateEmployee({ ...createEmployee, status: value })}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="active">Active</SelectItem>
                        <SelectItem value="inactive">Inactive</SelectItem>
                        <SelectItem value="on_leave">On Leave</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <DialogFooter>
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setIsCreateDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createEmployeeMutation.isPending}>
                    {createEmployeeMutation.isPending ? "Creating..." : "Create Employee"}
                  </Button>
                </DialogFooter>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Employees</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">{employees?.length || 0}</div>
            <p className="text-xs text-muted-foreground">
              Across {departments.length} departments
            </p>
          </CardContent>
        </Card>

        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active</CardTitle>
            <BadgeIcon className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">
              {employees?.filter(emp => emp.status === 'active').length || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Currently working
            </p>
          </CardContent>
        </Card>

        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">With Assets</CardTitle>
            <Laptop className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">
              {employees?.filter(emp => 
                assignments?.some(assignment => 
                  assignment.employeeId === emp.id && !assignment.returnedDate
                )
              ).length || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Have assigned assets
            </p>
          </CardContent>
        </Card>

        <Card className="glass-card border-0 glass-card border-0">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Departments</CardTitle>
            <Building2 className="h-4 w-4 text-purple-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white/90">{departments.length}</div>
            <p className="text-xs text-muted-foreground">
              Active departments
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card className="glass-card border-0 glass-card border-0">
        <CardHeader>
          <CardTitle className="text-lg">Search & Filter Employees</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-4 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground pointer-events-none z-10" />
              <Input
                placeholder="Search by Name, Employee Code, Email, Department..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="h-12 pl-12 pr-12 backdrop-blur-sm border-border/40 focus:border-primary/50 transition-all"
                data-testid="input-search-employees"
              />
              {searchTerm && (
                <button
                  onClick={() => setSearchTerm("")}
                  className="absolute right-4 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
                  data-testid="button-clear-search"
                >
                  <X className="h-4 w-4" />
                </button>
              )}
            </div>

            <Button
              onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
              variant="outline"
              className="h-12 gap-2 min-w-[180px] backdrop-blur-sm border-border/40 hover:border-primary/50 transition-all"
              data-testid="button-toggle-advanced-filters"
            >
              <SlidersHorizontal className="h-4 w-4" />
              Advanced Filters
              {activeFiltersCount > 0 && (
                <Badge variant="default" className="ml-1 px-2 py-0.5 text-xs">
                  {activeFiltersCount}
                </Badge>
              )}
            </Button>
          </div>

          {/* Quick Filters */}
          <div className="flex flex-wrap gap-2">
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-[140px] backdrop-blur-sm border-border/40" data-testid="select-filter-status">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="active">Active</SelectItem>
                <SelectItem value="inactive">Inactive</SelectItem>
                <SelectItem value="on_leave">On Leave</SelectItem>
              </SelectContent>
            </Select>

            {(departmentFilter !== "all" || locationFilter !== "all" || designationFilter !== "all") && (
              <Button
                onClick={() => {
                  setDepartmentFilter("all")
                  setLocationFilter("all")
                  setDesignationFilter("all")
                }}
                variant="ghost"
                className="gap-2 text-muted-foreground hover:text-foreground backdrop-blur-sm"
                data-testid="button-reset-filters"
              >
                <X className="h-4 w-4" />
                Clear All
              </Button>
            )}
          </div>

          {/* Advanced Filters Panel */}
          {showAdvancedFilters && (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4 rounded-lg bg-muted/10 backdrop-blur-sm border border-border/40 animate-fade-in">
              <div className="space-y-2">
                <label className="text-sm font-medium flex items-center gap-2">
                  <Filter className="h-4 w-4 text-primary" />
                  Department
                </label>
                <Select value={departmentFilter} onValueChange={setDepartmentFilter}>
                  <SelectTrigger className="backdrop-blur-sm border-border/40" data-testid="select-filter-department">
                    <SelectValue placeholder="All Departments" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Departments</SelectItem>
                    {departments.map(dept => (
                      <SelectItem key={dept} value={dept}>{dept}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium flex items-center gap-2">
                  <Filter className="h-4 w-4 text-primary" />
                  Location
                </label>
                <Select value={locationFilter} onValueChange={setLocationFilter}>
                  <SelectTrigger className="backdrop-blur-sm border-border/40" data-testid="select-filter-location">
                    <SelectValue placeholder="All Locations" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Locations</SelectItem>
                    {locations?.map(location => (
                      <SelectItem key={location.id} value={location.id.toString()}>
                        {location.outletName}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium flex items-center gap-2">
                  <Filter className="h-4 w-4 text-primary" />
                  Designation
                </label>
                <Select value={designationFilter} onValueChange={setDesignationFilter}>
                  <SelectTrigger className="backdrop-blur-sm border-border/40" data-testid="select-filter-designation">
                    <SelectValue placeholder="All Designations" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Designations</SelectItem>
                    {designations.map(designation => (
                      <SelectItem key={designation} value={designation}>{designation}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          )}

          <div className="text-sm text-muted-foreground">
            Showing {filteredEmployees.length} of {employees?.length || 0} employees
          </div>
        </CardContent>
      </Card>

      {/* Employees Table */}
      <Card className="glass-card border-0 glass-card border-0">
        <CardContent className="p-0 table-container-stable">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Employee</TableHead>
                <TableHead>Department</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Location</TableHead>
                <TableHead>Contact</TableHead>
                <TableHead>Assigned Assets</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredEmployees.flatMap((employee) => {
                const employeeAssets = getEmployeeAssets(employee.id)
                const isExpanded = expandedEmployeeId === employee.id
                const rows = [
                  <TableRow 
                    key={`main-${employee.id}`}
                    onClick={() => setExpandedEmployeeId(isExpanded ? null : employee.id)}
                    className="hover:bg-muted/20 transition-all duration-150 border-b border-border/30 group cursor-pointer"
                    data-testid={`row-employee-${employee.id}`}
                  >
                    <TableCell>
                      <div className="flex items-center gap-3">
                        <div className="flex items-center justify-center w-8 h-8 bg-muted/50 rounded-full group-hover:bg-muted transition-colors">
                          {isExpanded ? (
                            <ChevronUp className="h-4 w-4" />
                          ) : (
                            <ChevronDown className="h-4 w-4" />
                          )}
                        </div>
                        <User className="h-8 w-8 p-1.5 bg-muted rounded-full" />
                        <div>
                          <div className="font-medium">
                            {employee.firstName} {employee.lastName}
                          </div>
                          <div className="text-sm text-muted-foreground font-mono">
                            {employee.employeeCode}
                          </div>
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div>
                        <Badge variant="outline">{employee.department}</Badge>
                        <div className="text-sm text-muted-foreground mt-1">
                          {employee.designation}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <div 
                          className={`w-2 h-2 rounded-full ${statusColors[employee.status]}`}
                        />
                        <span className="capitalize">{employee.status.replace('_', ' ')}</span>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-1">
                        <MapPin className="h-3 w-3 text-muted-foreground" />
                        {getLocationName(employee.locationId)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="space-y-1">
                        <div className="flex items-center gap-1 text-sm">
                          <Mail className="h-3 w-3 text-muted-foreground" />
                          {employee.email}
                        </div>
                        <div className="flex items-center gap-1 text-sm">
                          <Phone className="h-3 w-3 text-muted-foreground" />
                          {employee.phone}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="space-y-1">
                        {employeeAssets?.length > 0 ? (
                          employeeAssets.map((asset, index) => (
                            <Badge key={index} variant="secondary" className="text-xs">
                              {asset?.assetId}
                            </Badge>
                          ))
                        ) : (
                          <span className="text-sm text-muted-foreground">No assets</span>
                        )}
                      </div>
                    </TableCell>
                  </TableRow>
                ]

                if (isExpanded) {
                  rows.push(
                    <TableRow key={`expanded-${employee.id}`} className="bg-muted/10 hover:bg-muted/10">
                      <TableCell colSpan={6} className="p-6">
                        <div className="grid grid-cols-3 gap-6">
                          <div className="space-y-4">
                            <h4 className="font-semibold text-sm uppercase tracking-wide text-muted-foreground">Employee Information</h4>
                            <div className="space-y-3">
                              <div>
                                <div className="text-xs text-muted-foreground">Employee Code</div>
                                <div className="font-mono text-sm mt-1">{employee.employeeCode}</div>
                              </div>
                              <div>
                                <div className="text-xs text-muted-foreground">Full Name</div>
                                <div className="text-sm mt-1">{employee.firstName} {employee.lastName}</div>
                              </div>
                              <div>
                                <div className="text-xs text-muted-foreground">Department</div>
                                <div className="text-sm mt-1">{employee.department}</div>
                              </div>
                              <div>
                                <div className="text-xs text-muted-foreground">Designation</div>
                                <div className="text-sm mt-1">{employee.designation}</div>
                              </div>
                            </div>
                          </div>

                          <div className="space-y-4">
                            <h4 className="font-semibold text-sm uppercase tracking-wide text-muted-foreground">Contact Information</h4>
                            <div className="space-y-3">
                              <div>
                                <div className="text-xs text-muted-foreground">Email Address</div>
                                <div className="text-sm mt-1 flex items-center gap-2">
                                  <Mail className="h-3 w-3" />
                                  {employee.email}
                                </div>
                              </div>
                              <div>
                                <div className="text-xs text-muted-foreground">Phone Number</div>
                                <div className="text-sm mt-1 flex items-center gap-2">
                                  <Phone className="h-3 w-3" />
                                  {employee.phone}
                                </div>
                              </div>
                              <div>
                                <div className="text-xs text-muted-foreground">Location</div>
                                <div className="text-sm mt-1 flex items-center gap-2">
                                  <MapPin className="h-3 w-3" />
                                  {getLocationName(employee.locationId)}
                                </div>
                              </div>
                              <div>
                                <div className="text-xs text-muted-foreground">Status</div>
                                <div className="text-sm mt-1 flex items-center gap-2">
                                  <div className={`w-2 h-2 rounded-full ${statusColors[employee.status]}`} />
                                  <span className="capitalize">{employee.status.replace('_', ' ')}</span>
                                </div>
                              </div>
                            </div>
                          </div>

                          <div className="space-y-4">
                            <h4 className="font-semibold text-sm uppercase tracking-wide text-muted-foreground">Assigned Assets</h4>
                            <div className="space-y-2">
                              {employeeAssets && employeeAssets.length > 0 ? (
                                employeeAssets.map((asset, index) => (
                                  <div key={index} className="p-3 bg-muted/30 rounded-lg">
                                    <div className="flex items-center gap-2 mb-1">
                                      <Laptop className="h-4 w-4 text-muted-foreground" />
                                      <span className="font-mono text-sm font-medium">{asset?.assetId}</span>
                                    </div>
                                    <div className="text-xs text-muted-foreground">
                                      {asset?.brand} {asset?.modelName}
                                    </div>
                                    <div className="text-xs text-muted-foreground mt-1">
                                      Type: {asset?.assetType}
                                    </div>
                                  </div>
                                ))
                              ) : (
                                <div className="text-sm text-muted-foreground italic p-3 bg-muted/20 rounded-lg">
                                  No assets currently assigned to this employee
                                </div>
                              )}
                            </div>
                          </div>
                        </div>

                        <div className="flex gap-2 mt-6 pt-4 border-t border-border/50">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={(e) => {
                              e.stopPropagation()
                              setSelectedEmployee(employee)
                              setIsViewDialogOpen(true)
                            }}
                            data-testid={`button-view-details-${employee.id}`}
                          >
                            <Eye className="mr-2 h-4 w-4" />
                            View Full Details
                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={(e) => {
                              e.stopPropagation()
                              setSelectedEmployee(employee)
                              setIsEditDialogOpen(true)
                            }}
                            data-testid={`button-edit-employee-${employee.id}`}
                          >
                            <Edit className="mr-2 h-4 w-4" />
                            Edit Employee
                          </Button>
                          <Button
                            variant="destructive"
                            size="sm"
                            onClick={(e) => {
                              e.stopPropagation()
                              if (window.confirm(`Are you sure you want to delete ${employee.firstName} ${employee.lastName}? This action cannot be undone.`)) {
                                deleteEmployeeMutation.mutate(employee.id)
                              }
                            }}
                            data-testid={`button-delete-employee-${employee.id}`}
                          >
                            <Trash2 className="mr-2 h-4 w-4" />
                            Delete Employee
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  )
                }

                return rows
              })}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* View Employee Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Employee Details</DialogTitle>
            <DialogDescription>
              View personnel profile, contact information, and resource allocation for {selectedEmployee?.firstName} {selectedEmployee?.lastName}
            </DialogDescription>
          </DialogHeader>
          {selectedEmployee && (
            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Employee Code</Label>
                  <div className="text-sm p-2 bg-muted rounded font-mono">
                    {selectedEmployee.employeeCode}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Full Name</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedEmployee.firstName} {selectedEmployee.lastName}
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Department</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedEmployee.department}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Designation</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedEmployee.designation}
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Email</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedEmployee.email}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Phone</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedEmployee.phone}
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Status</Label>
                  <div className="text-sm p-2 bg-muted rounded flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${statusColors[selectedEmployee.status]}`} />
                    <span className="capitalize">{selectedEmployee.status.replace('_', ' ')}</span>
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Location</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {getLocationName(selectedEmployee.locationId)}
                  </div>
                </div>
              </div>

              <div className="space-y-2">
                <Label className="text-sm font-medium">Assigned Assets</Label>
                <div className="p-2 bg-muted rounded">
                  {getEmployeeAssets(selectedEmployee.id)?.length > 0 ? (
                    <div className="flex flex-wrap gap-2">
                      {getEmployeeAssets(selectedEmployee.id).map((asset, index) => (
                        <Badge key={index} variant="secondary">
                          {asset?.assetId} ({asset?.brand} {asset?.modelName})
                        </Badge>
                      ))}
                    </div>
                  ) : (
                    <span className="text-sm text-muted-foreground">No assets assigned</span>
                  )}
                </div>
              </div>
            </div>
          )}
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsViewDialogOpen(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Employee Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit Employee</DialogTitle>
            <DialogDescription>
              Modify personnel details, department assignment, and contact information for {selectedEmployee?.firstName} {selectedEmployee?.lastName}
            </DialogDescription>
          </DialogHeader>
          {selectedEmployee && (
            <form onSubmit={handleUpdateEmployee} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-firstName">First Name *</Label>
                  <Input
                    id="edit-firstName"
                    value={editEmployee.firstName}
                    onChange={(e) => setEditEmployee({ ...editEmployee, firstName: e.target.value })}
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-lastName">Last Name *</Label>
                  <Input
                    id="edit-lastName"
                    value={editEmployee.lastName}
                    onChange={(e) => setEditEmployee({ ...editEmployee, lastName: e.target.value })}
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-department">Department *</Label>
                  <Select value={editEmployee.department} onValueChange={(value) => setEditEmployee({ ...editEmployee, department: value })} required>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="IT">IT</SelectItem>
                      <SelectItem value="Sales">Sales</SelectItem>
                      <SelectItem value="Marketing">Marketing</SelectItem>
                      <SelectItem value="Operations">Operations</SelectItem>
                      <SelectItem value="Finance">Finance</SelectItem>
                      <SelectItem value="HR">HR</SelectItem>
                      <SelectItem value="Management">Management</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-designation">Designation *</Label>
                  <Input
                    id="edit-designation"
                    value={editEmployee.designation}
                    onChange={(e) => setEditEmployee({ ...editEmployee, designation: e.target.value })}
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-email">Email Address *</Label>
                  <Input
                    id="edit-email"
                    type="email"
                    value={editEmployee.email}
                    onChange={(e) => setEditEmployee({ ...editEmployee, email: e.target.value })}
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-phone">Phone Number *</Label>
                  <Input
                    id="edit-phone"
                    value={editEmployee.phone}
                    onChange={(e) => setEditEmployee({ ...editEmployee, phone: e.target.value })}
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-locationId">Location</Label>
                  <Select value={editEmployee.locationId} onValueChange={(value) => setEditEmployee({ ...editEmployee, locationId: value })}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select location" />
                    </SelectTrigger>
                    <SelectContent>
                      {locations?.map(location => (
                        <SelectItem key={location.id} value={location.id.toString()}>
                          {location.outletName}, {location.city}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-status">Status</Label>
                  <Select value={editEmployee.status} onValueChange={(value: any) => setEditEmployee({ ...editEmployee, status: value })}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="active">Active</SelectItem>
                      <SelectItem value="inactive">Inactive</SelectItem>
                      <SelectItem value="on_leave">On Leave</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <DialogFooter>
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsEditDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button type="submit" disabled={updateEmployeeMutation.isPending}>
                  {updateEmployeeMutation.isPending ? "Updating..." : "Update Employee"}
                </Button>
              </DialogFooter>
            </form>
          )}
        </DialogContent>
      </Dialog>
    </div>
  )
}

