                          </Button>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={(e) => {
                              e.stopPropagation()
                              setSelectedMaintenance(record)
                              setIsEditDialogOpen(true)
                            }}
                            data-testid={`button-edit-maintenance-${record.id}`}
                          >
                            <Edit className="mr-2 h-4 w-4" />
