import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { 
  Building2, 
  Users, 
  Package, 
  MapPin, 
  Settings, 
  Shield, 
  Database,
  Bell,
  Upload,
  Download,
  Trash2,
  Edit,
  Plus,
  Check,
  X,
  Link as LinkIcon,
  Key,
  Lock,
  Eye,
  EyeOff
} from "lucide-react";
import { Separator } from "@/components/ui/separator";
import { SidebarTrigger } from "@/components/ui/sidebar";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Link } from "wouter";
import { useUser } from "@/hooks/use-user";

interface CompanySettings {
  companyName: string;
  logoUrl: string;
  timezone: string;
  dateFormat: string;
  language: string;
}

interface User {
  id: number;
  username: string;
  email: string;
  firstName: string;
  lastName: string;
  role: string;
  status: string;
  lastLogin: string | null;
  locationId: number | null;
}

interface AssetType {
  id: number;
  name: string;
  description: string | null;
  isActive: boolean;
}

export default function SettingsPage() {
  const { toast } = useToast();
  const { data: currentUser } = useUser();

  // Fetch company settings
  const { data: companySettings, isLoading: loadingCompanySettings } = useQuery<CompanySettings>({
    queryKey: ["/api/settings/company"],
  });

  // Fetch users
  const { data: users, isLoading: loadingUsers } = useQuery<User[]>({
    queryKey: ["/api/users"],
    enabled: currentUser?.user?.role === 'super_admin' || currentUser?.user?.role === 'admin',
  });

  // Fetch asset types
  const { data: assetTypes, isLoading: loadingAssetTypes } = useQuery<AssetType[]>({
    queryKey: ["/api/asset-types"],
  });

  const [editingCompanySettings, setEditingCompanySettings] = useState<CompanySettings | null>(null);
  const [newUser, setNewUser] = useState({
    username: "",
    email: "",
    firstName: "",
    lastName: "",
    role: "user",
    password: "",
    locationId: null as number | null,
  });
  const [newAssetType, setNewAssetType] = useState({
    name: "",
    description: "",
  });

  // Password reset state (self-service)
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Admin password reset state
  const [adminResetDialog, setAdminResetDialog] = useState<{ open: boolean; user: User | null }>({
    open: false,
    user: null,
  });
  const [adminResetPassword, setAdminResetPassword] = useState("");
  const [adminResetConfirmPassword, setAdminResetConfirmPassword] = useState("");
  const [showAdminResetPassword, setShowAdminResetPassword] = useState(false);
  const [showAdminResetConfirmPassword, setShowAdminResetConfirmPassword] = useState(false);

  // Update company settings mutation
  const updateCompanySettingsMutation = useMutation({
    mutationFn: async (settings: CompanySettings) => {
      return await apiRequest("PATCH", "/api/settings/company", settings);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings/company"] });
      toast({
        title: "Success",
        description: "Company settings updated successfully",
      });
      setEditingCompanySettings(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update company settings",
        variant: "destructive",
      });
    },
  });

  // Create user mutation
  const createUserMutation = useMutation({
    mutationFn: async (userData: typeof newUser) => {
      return await apiRequest("POST", "/api/users", userData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User created successfully",
      });
      setNewUser({ 
        username: "", 
        email: "", 
        firstName: "", 
        lastName: "", 
        role: "user", 
        password: "",
        locationId: null,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create user",
        variant: "destructive",
      });
    },
  });

  // Update user mutation
  const updateUserMutation = useMutation({
    mutationFn: async ({ id, status }: { id: number; status: string }) => {
      return await apiRequest("PATCH", `/api/users/${id}`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User status updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update user status",
        variant: "destructive",
      });
    },
  });

  // Create asset type mutation
  const createAssetTypeMutation = useMutation({
    mutationFn: async (assetTypeData: typeof newAssetType) => {
      return await apiRequest("POST", "/api/asset-types", assetTypeData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/asset-types"] });
      toast({
        title: "Success",
        description: "Asset type created successfully",
      });
      setNewAssetType({ name: "", description: "" });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create asset type",
        variant: "destructive",
      });
    },
  });

  // Password reset mutation (self-service)
  const passwordResetMutation = useMutation({
    mutationFn: async (data: typeof passwordData) => {
      return await apiRequest("POST", "/api/auth/reset-password", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Password updated successfully",
      });
      setPasswordData({
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update password",
        variant: "destructive",
      });
    },
  });

  // Admin password reset mutation
  const adminPasswordResetMutation = useMutation({
    mutationFn: async ({ userId, newPassword, confirmPassword }: { userId: number; newPassword: string; confirmPassword: string }) => {
      return await apiRequest("POST", `/api/users/${userId}/reset-password`, { newPassword, confirmPassword });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: `Password reset successfully for ${adminResetDialog.user?.username}`,
      });
      setAdminResetDialog({ open: false, user: null });
      setAdminResetPassword("");
      setAdminResetConfirmPassword("");
      setShowAdminResetPassword(false);
      setShowAdminResetConfirmPassword(false);
      queryClient.invalidateQueries({ queryKey: ['/api/users'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reset password",
        variant: "destructive",
      });
    },
  });

  const handleSaveCompanySettings = () => {
    if (editingCompanySettings) {
      updateCompanySettingsMutation.mutate(editingCompanySettings);
    }
  };

  const handleCreateUser = () => {
    if (!newUser.username || !newUser.email || !newUser.firstName || !newUser.lastName || !newUser.password) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    if (newUser.password.length < 8) {
      toast({
        title: "Validation Error",
        description: "Password must be at least 8 characters",
        variant: "destructive",
      });
      return;
    }

    createUserMutation.mutate(newUser);
  };

  const handleToggleUserStatus = (userId: number, currentStatus: string) => {
    const newStatus = currentStatus === 'active' ? 'inactive' : 'active';
    updateUserMutation.mutate({ id: userId, status: newStatus });
  };

  const handleCreateAssetType = () => {
    if (!newAssetType.name) {
      toast({
        title: "Validation Error",
        description: "Asset type name is required",
        variant: "destructive",
      });
      return;
    }

    createAssetTypeMutation.mutate(newAssetType);
  };

  const handlePasswordReset = () => {
    // Validate fields
    if (!passwordData.currentPassword || !passwordData.newPassword || !passwordData.confirmPassword) {
      toast({
        title: "Validation Error",
        description: "All password fields are required",
        variant: "destructive",
      });
      return;
    }

    // Check password length
    if (passwordData.newPassword.length < 8) {
      toast({
        title: "Validation Error",
        description: "New password must be at least 8 characters",
        variant: "destructive",
      });
      return;
    }

    // Check passwords match
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast({
        title: "Validation Error",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    // Check if new password is different from current
    if (passwordData.newPassword === passwordData.currentPassword) {
      toast({
        title: "Validation Error",
        description: "New password must be different from current password",
        variant: "destructive",
      });
      return;
    }

    passwordResetMutation.mutate(passwordData);
  };

  const handleAdminPasswordReset = () => {
    if (!adminResetPassword || !adminResetConfirmPassword) {
      toast({
        title: "Validation Error",
        description: "All password fields are required",
        variant: "destructive",
      });
      return;
    }

    if (adminResetPassword.length < 8) {
      toast({
        title: "Validation Error",
        description: "Password must be at least 8 characters",
        variant: "destructive",
      });
      return;
    }

    if (adminResetPassword !== adminResetConfirmPassword) {
      toast({
        title: "Validation Error",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    if (!adminResetDialog.user) {
      return;
    }

    adminPasswordResetMutation.mutate({
      userId: adminResetDialog.user.id,
      newPassword: adminResetPassword,
      confirmPassword: adminResetConfirmPassword,
    });
  };

  const canResetUserPassword = (targetUser: User) => {
    // Cannot reset own password via admin endpoint
    if (targetUser.id === currentUser?.user?.id) {
      return false;
    }

    // Super admin can reset anyone's password (except their own)
    if (currentUser?.user?.role === 'super_admin') {
      return true;
    }

    // Admin cannot reset super admin passwords
    if (currentUser?.user?.role === 'admin' && targetUser.role !== 'super_admin') {
      return true;
    }

    return false;
  };

  const isSuperAdmin = currentUser?.user?.role === 'super_admin';
  const isAdmin = currentUser?.user?.role === 'admin' || isSuperAdmin;

  return (
    <div className="min-h-screen glass-bg p-4 md:p-8">
      <div className="max-w-7xl mx-auto space-y-6">
        <SidebarTrigger data-testid="button-sidebar-toggle" className="mb-4 text-white/80 hover:text-white hover:bg-white/10 rounded-md" />

        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-white/90 flex items-center gap-3">
              <Settings className="h-8 w-8 text-purple-400" />
              System Settings
            </h1>
            <p className="text-white/60 mt-1">Manage system configuration and preferences</p>
          </div>
        </div>

        {/* Settings Tabs */}
        <Tabs defaultValue="company" className="space-y-6">
          <TabsList className="glass-card">
            <TabsTrigger value="company" data-testid="tab-company">
              <Building2 className="h-4 w-4 mr-2" />
              Company
            </TabsTrigger>
            {isAdmin && (
              <TabsTrigger value="users" data-testid="tab-users">
                <Users className="h-4 w-4 mr-2" />
                Users
              </TabsTrigger>
            )}
            <TabsTrigger value="assets" data-testid="tab-assets">
              <Package className="h-4 w-4 mr-2" />
              Asset Types
            </TabsTrigger>
            <TabsTrigger value="locations" data-testid="tab-locations">
              <MapPin className="h-4 w-4 mr-2" />
              Locations
            </TabsTrigger>
            <TabsTrigger value="security" data-testid="tab-security">
              <Shield className="h-4 w-4 mr-2" />
              Security
            </TabsTrigger>
          </TabsList>

          {/* Company Settings */}
          <TabsContent value="company" className="space-y-6">
            <Card className="glass-card border-0">
              <CardHeader>
                <CardTitle className="flex items-center gap-2 text-white/90">
                  <Building2 className="h-5 w-5 text-purple-400" />
                  Company Information
                </CardTitle>
                <CardDescription className="text-white/60">
                  Update your organization details and preferences
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                {loadingCompanySettings ? (
                  <div className="text-white/60">Loading company settings...</div>
                ) : (
                  <>
                    <div className="grid gap-4 md:grid-cols-2">
                      <div className="space-y-2">
                        <Label htmlFor="companyName" className="text-white/80">Company Name</Label>
                        <Input
                          id="companyName"
                          value={editingCompanySettings?.companyName || companySettings?.companyName || ""}
                          onChange={(e) => setEditingCompanySettings({
                            ...(editingCompanySettings || companySettings || {} as CompanySettings),
                            companyName: e.target.value
                          })}
                          placeholder="BODYCRAFT"
                          className="glass-input"
                          data-testid="input-company-name"
                        />
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="timezone" className="text-white/80">Timezone</Label>
                        <Select
                          value={editingCompanySettings?.timezone || companySettings?.timezone || "Asia/Kolkata"}
                          onValueChange={(value) => setEditingCompanySettings({
                            ...(editingCompanySettings || companySettings || {} as CompanySettings),
                            timezone: value
                          })}
                        >
                          <SelectTrigger className="glass-input" data-testid="select-timezone">
                            <SelectValue placeholder="Select timezone" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="Asia/Kolkata">Asia/Kolkata (IST)</SelectItem>
                            <SelectItem value="UTC">UTC</SelectItem>
                            <SelectItem value="America/New_York">America/New York (EST)</SelectItem>
                            <SelectItem value="Europe/London">Europe/London (GMT)</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="dateFormat" className="text-white/80">Date Format</Label>
                        <Select
                          value={editingCompanySettings?.dateFormat || companySettings?.dateFormat || "DD/MM/YYYY"}
                          onValueChange={(value) => setEditingCompanySettings({
                            ...(editingCompanySettings || companySettings || {} as CompanySettings),
                            dateFormat: value
                          })}
                        >
                          <SelectTrigger className="glass-input" data-testid="select-date-format">
                            <SelectValue placeholder="Select date format" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="DD/MM/YYYY">DD/MM/YYYY</SelectItem>
                            <SelectItem value="MM/DD/YYYY">MM/DD/YYYY</SelectItem>
                            <SelectItem value="YYYY-MM-DD">YYYY-MM-DD</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="language" className="text-white/80">Language</Label>
                        <Select
                          value={editingCompanySettings?.language || companySettings?.language || "en"}
                          onValueChange={(value) => setEditingCompanySettings({
                            ...(editingCompanySettings || companySettings || {} as CompanySettings),
                            language: value
                          })}
                        >
                          <SelectTrigger className="glass-input" data-testid="select-language">
                            <SelectValue placeholder="Select language" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="en">English</SelectItem>
                            <SelectItem value="hi">Hindi</SelectItem>
                            <SelectItem value="ta">Tamil</SelectItem>
                            <SelectItem value="te">Telugu</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </div>

                    <div className="flex gap-3">
                      <Button 
                        onClick={handleSaveCompanySettings} 
                        disabled={!editingCompanySettings || updateCompanySettingsMutation.isPending}
                        className="bg-gradient-to-r from-purple-500 to-blue-500"
                        data-testid="button-save-company-settings"
                      >
                        {updateCompanySettingsMutation.isPending ? "Saving..." : "Save Changes"}
                      </Button>
                      {editingCompanySettings && (
                        <Button 
                          variant="outline" 
                          onClick={() => setEditingCompanySettings(null)}
                          data-testid="button-cancel-company-settings"
                        >
                          Cancel
                        </Button>
                      )}
                    </div>
                  </>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* User Management */}
          {isAdmin && (
            <TabsContent value="users" className="space-y-6">
              <Card className="glass-card border-0">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2 text-white/90">
                    <Users className="h-5 w-5 text-purple-400" />
                    User Management
                  </CardTitle>
                  <CardDescription className="text-white/60">
                    Create and manage user accounts
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                  {/* Create New User Form - Super Admin Only */}
                  {isSuperAdmin && (
                    <div className="p-6 rounded-lg bg-white/5 border border-white/10 space-y-4">
                      <h3 className="text-lg font-semibold text-white/90 flex items-center gap-2">
                        <Plus className="h-5 w-5 text-purple-400" />
                        Create New User
                      </h3>

                      <div className="grid gap-4 md:grid-cols-2">
                        <div className="space-y-2">
                          <Label htmlFor="new-username" className="text-white/80">Username *</Label>
                          <Input
                            id="new-username"
                            value={newUser.username}
                            onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
                            placeholder="Enter username"
                            className="glass-input"
                            data-testid="input-new-username"
                          />
                        </div>

                        <div className="space-y-2">
                          <Label htmlFor="new-email" className="text-white/80">Email *</Label>
                          <Input
                            id="new-email"
                            type="email"
                            value={newUser.email}
                            onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                            placeholder="Enter email"
                            className="glass-input"
                            data-testid="input-new-email"
