interface Asset {
  assetId: string
  modelName: string
  brand: string
  serviceTag: string | null
  assetType: string
  purchaseDate: string | null
  warrantyExpiry: string | null
  status: "available" | "assigned" | "maintenance" | "retired"
  condition: "excellent" | "good" | "fair" | "poor"
  locationId: number | null
