}

export default function AssetsPage() {
  const [searchTerm, setSearchTerm] = useState("")
  const [statusFilter, setStatusFilter] = useState<string>("all")
  const [typeFilter, setTypeFilter] = useState<string>("all")
  const [locationFilter, setLocationFilter] = useState<string>("all")
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false)
  const [selectedAsset, setSelectedAsset] = useState<Asset | null>(null)
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false)
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false)
  const [isImportDialogOpen, setIsImportDialogOpen] = useState(false)
  const [importFile, setImportFile] = useState<File | null>(null)
  const [importErrors, setImportErrors] = useState<string[]>([])
  const [isImporting, setIsImporting] = useState(false)

  const { toast } = useToast()
  const queryClient = useQueryClient()

  // Fetch data
  const { data: assets, isLoading: assetsLoading } = useQuery<Asset[]>({
    queryKey: ["/api/assets"],
  })

  const { data: locations } = useQuery<Location[]>({
    queryKey: ["/api/locations"],
  })

  const { data: employees } = useQuery<Employee[]>({
    queryKey: ["/api/employees"],
  })

  // Get unique asset types for filter
  const assetTypes = [...new Set(assets?.map(asset => asset.assetType) || [])]

  // Filter assets
  const filteredAssets = assets?.filter(asset => {
    const matchesSearch = 
      asset.assetId.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.modelName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      asset.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (asset.serviceTag?.toLowerCase().includes(searchTerm.toLowerCase()) || false)

    const matchesStatus = statusFilter === "all" || asset.status === statusFilter
    const matchesType = typeFilter === "all" || asset.assetType === typeFilter
    const matchesLocation = locationFilter === "all" || asset.locationId?.toString() === locationFilter

    return matchesSearch && matchesStatus && matchesType && matchesLocation
  }) || []

  // Create asset mutation
  const createAssetMutation = useMutation({
    mutationFn: async (assetData: any) => {
      const response = await fetch('/api/assets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(assetData),
      })
      if (!response.ok) throw new Error('Failed to create asset')
      return response.json()
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/assets'] })
      toast({ title: "Success", description: "Asset created successfully" })
      setIsCreateDialogOpen(false)
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create asset", variant: "destructive" })
    }
  })

  // Update asset mutation  
  const updateAssetMutation = useMutation({
    mutationFn: async ({ assetId, data }: { assetId: string, data: any }) => {
      const response = await fetch(`/api/assets/${assetId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      })
      if (!response.ok) throw new Error('Failed to update asset')
      return response.json()
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/assets'] })
      toast({ title: "Success", description: "Asset updated successfully" })
      setIsEditDialogOpen(false)
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update asset", variant: "destructive" })
    }
  })

  // Delete asset mutation
  const deleteAssetMutation = useMutation({
    mutationFn: async (assetId: string) => {
      const response = await fetch(`/api/assets/${assetId}`, {
        method: 'DELETE',
      })
      if (!response.ok) throw new Error('Failed to delete asset')
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/assets'] })
      toast({ title: "Success", description: "Asset deleted successfully" })
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete asset", variant: "destructive" })
    }
  })

  // Helper functions
  const getLocationName = (locationId: number | null) => {
    if (!locationId) return "No location"
    const location = locations?.find(loc => loc.id === locationId)
    return location ? `${location.outletName}, ${location.city}` : "Unknown location"
  }

  const getEmployeeName = (employeeId: number | null) => {
    if (!employeeId) return "Unassigned"
    const employee = employees?.find(emp => emp.id === employeeId)
    return employee ? `${employee.firstName} ${employee.lastName}` : "Unknown employee"
  }

  const getAssetIcon = (assetType: string) => {
    const IconComponent = assetTypeIcons[assetType] || assetTypeIcons.default
    return <IconComponent className="h-4 w-4" />
  }

  const handleCreateAsset = (event: React.FormEvent) => {
    event.preventDefault()
    const formData = new FormData(event.target as HTMLFormElement)

    const assetData = {
      assetId: formData.get('assetId'),
      modelName: formData.get('modelName'),
      brand: formData.get('brand'),
      serviceTag: formData.get('serviceTag') || null,
      assetType: formData.get('assetType'),
      purchaseDate: formData.get('purchaseDate') || null,
      warrantyExpiry: formData.get('warrantyExpiry') || null,
      status: formData.get('status'),
      condition: formData.get('condition'),
      locationId: formData.get('locationId') ? parseInt(formData.get('locationId') as string) : null,
    }

    createAssetMutation.mutate(assetData)
  }

  const handleUpdateAsset = (event: React.FormEvent) => {
    event.preventDefault()
    if (!selectedAsset) return

    const formData = new FormData(event.target as HTMLFormElement)

    const assetData = {
      modelName: formData.get('modelName'),
      brand: formData.get('brand'),
      serviceTag: formData.get('serviceTag') || null,
      assetType: formData.get('assetType'),
      purchaseDate: formData.get('purchaseDate') || null,
      warrantyExpiry: formData.get('warrantyExpiry') || null,
      status: formData.get('status'),
      condition: formData.get('condition'),
      locationId: formData.get('locationId') ? parseInt(formData.get('locationId') as string) : null,
    }

    updateAssetMutation.mutate({ assetId: selectedAsset.assetId, data: assetData })
  }

  // Excel Export Handler
  const handleExportAssets = () => {
    if (!filteredAssets || !locations) {
      toast({ title: "Error", description: "No data available to export", variant: "destructive" })
      return
    }

    ExcelExporter.exportAssets(filteredAssets, locations)
    toast({ title: "Success", description: `Exported ${filteredAssets.length} assets to Excel` })
  }

  // Excel Import Handler
  const handleImportAssets = async () => {
    if (!importFile) {
      toast({ title: "Error", description: "Please select a file to import", variant: "destructive" })
      return
    }

    setIsImporting(true)
    setImportErrors([])

    try {
      // Parse Excel file
      const data = await ExcelImporter.parseExcelFile(importFile)

      if (data.length === 0) {
        toast({ title: "Error", description: "The Excel file is empty", variant: "destructive" })
        setIsImporting(false)
        return
      }

      // Validate data
      const { valid, errors } = ExcelImporter.validateAssetData(data)

      if (errors.length > 0) {
        setImportErrors(errors)
        setIsImporting(false)
        return
      }

      // Import valid assets
      let successCount = 0
      let errorCount = 0

      for (const assetData of valid) {
        try {
          const response = await fetch('/api/assets', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(assetData),
          })

          if (response.ok) {
            successCount++
          } else {
            errorCount++
            const errorData = await response.json()
            setImportErrors(prev => [...prev, `Asset ${assetData.assetId}: ${errorData.message || 'Import failed'}`])
          }
        } catch (error) {
          errorCount++
          setImportErrors(prev => [...prev, `Asset ${assetData.assetId}: Network error`])
        }
      }

      // Refresh data
      queryClient.invalidateQueries({ queryKey: ['/api/assets'] })

      if (successCount > 0) {
        toast({ 
          title: "Import Complete", 
          description: `Successfully imported ${successCount} assets${errorCount > 0 ? `, ${errorCount} failed` : ''}` 
        })

        if (errorCount === 0) {
          setIsImportDialogOpen(false)
          setImportFile(null)
        }
      } else {
        toast({ title: "Import Failed", description: "No assets were imported", variant: "destructive" })
      }

    } catch (error) {
      toast({ title: "Error", description: "Failed to parse Excel file", variant: "destructive" })
      setImportErrors(['Failed to parse Excel file. Please ensure it\'s a valid Excel file.'])
    } finally {
      setIsImporting(false)
    }
  }

  if (assetsLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-1/4 mb-6"></div>
          <div className="space-y-4">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="h-16 bg-muted rounded"></div>
            ))}
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Assets</h1>
          <p className="text-muted-foreground">
            Manage all BODYCRAFT IT assets across {locations?.length || 0} locations
          </p>
        </div>
        <div className="flex gap-2">
          <Dialog open={isImportDialogOpen} onOpenChange={setIsImportDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline" className="gap-2">
                <Upload className="h-4 w-4" />
                Import Excel
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Import Assets from Excel</DialogTitle>
                <DialogDescription>
                  Upload an Excel file to import multiple assets. Download the template first if you need the correct format.
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div className="flex gap-2">
                  <Button 
                    variant="outline" 
                    onClick={() => ExcelImporter.downloadAssetTemplate()}
                    className="gap-2"
                  >
                    <Download className="h-4 w-4" />
                    Download Template
                  </Button>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="excel-file">Excel File *</Label>
                  <Input
                    id="excel-file"
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={(e) => {
                      const file = e.target.files?.[0]
                      if (file) {
                        setImportFile(file)
                        setImportErrors([])
                      }
                    }}
                  />
                </div>

                {importErrors.length > 0 && (
                  <div className="space-y-2">
                    <Label className="text-red-600">Import Errors:</Label>
                    <div className="bg-red-50 p-3 rounded max-h-32 overflow-y-auto">
                      {importErrors.map((error, index) => (
                        <div key={index} className="text-sm text-red-600">â€¢ {error}</div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
              <DialogFooter>
                <Button 
                  variant="outline" 
                  onClick={() => {
                    setIsImportDialogOpen(false)
                    setImportFile(null)
                    setImportErrors([])
                  }}
                >
                  Cancel
                </Button>
                <Button 
                  onClick={handleImportAssets}
                  disabled={!importFile || isImporting}
                >
                  {isImporting ? "Importing..." : "Import Assets"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
          <Button 
            variant="outline" 
            className="gap-2"
            onClick={handleExportAssets}
          >
            <Download className="h-4 w-4" />
            Export
          </Button>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="gap-2">
                <Plus className="h-4 w-4" />
                Add Asset
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New Asset</DialogTitle>
                <DialogDescription>
                  Add a new IT asset to the BODYCRAFT inventory
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={handleCreateAsset} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="assetId">Asset ID *</Label>
                    <Input
                      id="assetId"
                      name="assetId"
                      placeholder="BFC001"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="assetType">Asset Type *</Label>
                    <Select name="assetType" required>
                      <SelectTrigger>
                        <SelectValue placeholder="Select type" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="Laptop">Laptop</SelectItem>
                        <SelectItem value="Desktop">Desktop</SelectItem>
                        <SelectItem value="Monitor">Monitor</SelectItem>
                        <SelectItem value="Mobile">Mobile</SelectItem>
                        <SelectItem value="Tablet">Tablet</SelectItem>
                        <SelectItem value="Router">Router</SelectItem>
                        <SelectItem value="Switch">Switch</SelectItem>
                        <SelectItem value="Printer">Printer</SelectItem>
                        <SelectItem value="UPS">UPS</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="brand">Brand *</Label>
                    <Input
                      id="brand"
                      name="brand"
                      placeholder="Dell, HP, Lenovo..."
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="modelName">Model Name *</Label>
                    <Input
                      id="modelName"
                      name="modelName"
                      placeholder="ThinkPad E15, OptiPlex 3080..."
                      required
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="serviceTag">Service Tag</Label>
                    <Input
                      id="serviceTag"
                      name="serviceTag"
                      placeholder="Manufacturer service tag"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="locationId">Location</Label>
                    <Select name="locationId">
                      <SelectTrigger>
                        <SelectValue placeholder="Select location" />
                      </SelectTrigger>
                      <SelectContent>
                        {locations?.map(location => (
                          <SelectItem key={location.id} value={location.id.toString()}>
                            {location.outletName}, {location.city}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="purchaseDate">Purchase Date</Label>
                    <Input
                      id="purchaseDate"
                      name="purchaseDate"
                      type="date"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="warrantyExpiry">Warranty Expiry</Label>
                    <Input
                      id="warrantyExpiry"
                      name="warrantyExpiry"
                      type="date"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="status">Status</Label>
                    <Select name="status" defaultValue="available">
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="available">Available</SelectItem>
                        <SelectItem value="assigned">Assigned</SelectItem>
                        <SelectItem value="maintenance">Maintenance</SelectItem>
                        <SelectItem value="retired">Retired</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="condition">Condition</Label>
                    <Select name="condition" defaultValue="good">
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="excellent">Excellent</SelectItem>
                        <SelectItem value="good">Good</SelectItem>
                        <SelectItem value="fair">Fair</SelectItem>
                        <SelectItem value="poor">Poor</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <DialogFooter>
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setIsCreateDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createAssetMutation.isPending}>
                    {createAssetMutation.isPending ? "Creating..." : "Create Asset"}
                  </Button>
                </DialogFooter>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Assets</CardTitle>
            <Package className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{assets?.length || 0}</div>
            <p className="text-xs text-muted-foreground">
              Across {locations?.length || 0} locations
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Available</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {assets?.filter(a => a.status === 'available').length || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Ready for assignment
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Assigned</CardTitle>
            <User className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {assets?.filter(a => a.status === 'assigned').length || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Currently in use
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Maintenance</CardTitle>
            <Clock className="h-4 w-4 text-yellow-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {assets?.filter(a => a.status === 'maintenance').length || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Under service
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Search & Filter Assets</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search by Asset ID, Model, Brand, or Service Tag..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-8"
                />
              </div>
            </div>
            <div className="flex gap-2">
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-[140px]">
                  <SelectValue placeholder="Status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="available">Available</SelectItem>
                  <SelectItem value="assigned">Assigned</SelectItem>
                  <SelectItem value="maintenance">Maintenance</SelectItem>
                  <SelectItem value="retired">Retired</SelectItem>
                </SelectContent>
              </Select>

              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="w-[140px]">
                  <SelectValue placeholder="Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  {assetTypes.map(type => (
                    <SelectItem key={type} value={type}>{type}</SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={locationFilter} onValueChange={setLocationFilter}>
                <SelectTrigger className="w-[180px]">
                  <SelectValue placeholder="Location" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Locations</SelectItem>
                  {locations?.map(location => (
                    <SelectItem key={location.id} value={location.id.toString()}>
                      {location.outletName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="text-sm text-muted-foreground">
            Showing {filteredAssets.length} of {assets?.length || 0} assets
          </div>
        </CardContent>
      </Card>

      {/* Assets Table */}
      <Card>
        <CardContent className="p-0">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Asset</TableHead>
                <TableHead>Type</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Condition</TableHead>
                <TableHead>Location</TableHead>
                <TableHead>Assigned To</TableHead>
                <TableHead>Service Tag</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {filteredAssets.map((asset) => (
                <TableRow key={asset.assetId}>
                  <TableCell>
                    <div className="flex items-center gap-3">
                      {getAssetIcon(asset.assetType)}
                      <div>
                        <div className="font-medium">{asset.assetId}</div>
                        <div className="text-sm text-muted-foreground">
                          {asset.brand} {asset.modelName}
                        </div>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <Badge variant="outline">{asset.assetType}</Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <div 
                        className={`w-2 h-2 rounded-full ${statusColors[asset.status]}`}
                      />
                      <span className="capitalize">{asset.status}</span>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <div 
                        className={`w-2 h-2 rounded-full ${conditionColors[asset.condition]}`}
                      />
                      <span className="capitalize">{asset.condition}</span>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <MapPin className="h-3 w-3 text-muted-foreground" />
                      {getLocationName(asset.locationId)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-1">
                      <User className="h-3 w-3 text-muted-foreground" />
                      {getEmployeeName(asset.currentUserId)}
                    </div>
                  </TableCell>
                  <TableCell>
                    <span className="text-sm font-mono">
                      {asset.serviceTag || "â€”"}
                    </span>
                  </TableCell>
                  <TableCell>
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant="ghost" className="h-8 w-8 p-0">
                          <MoreHorizontal className="h-4 w-4" />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent align="end">
                        <DropdownMenuLabel>Actions</DropdownMenuLabel>
                        <DropdownMenuItem
                          onClick={() => {
                            setSelectedAsset(asset)
                            setIsViewDialogOpen(true)
                          }}
                        >
                          <Eye className="mr-2 h-4 w-4" />
                          View Details
                        </DropdownMenuItem>
                        <DropdownMenuItem
                          onClick={() => {
                            setSelectedAsset(asset)
                            setIsEditDialogOpen(true)
                          }}
                        >
                          <Edit className="mr-2 h-4 w-4" />
                          Edit Asset
                        </DropdownMenuItem>
                        <DropdownMenuSeparator />
                        <DropdownMenuItem
                          onClick={() => deleteAssetMutation.mutate(asset.assetId)}
                          className="text-red-600"
                        >
                          <Trash2 className="mr-2 h-4 w-4" />
                          Delete Asset
                        </DropdownMenuItem>
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* View Asset Dialog */}
      <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Asset Details</DialogTitle>
            <DialogDescription>
              Complete information for {selectedAsset?.assetId}
            </DialogDescription>
          </DialogHeader>
          {selectedAsset && (
            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Asset ID</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedAsset.assetId}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Type</Label>
                  <div className="text-sm p-2 bg-muted rounded flex items-center gap-2">
                    {getAssetIcon(selectedAsset.assetType)}
                    {selectedAsset.assetType}
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Brand</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedAsset.brand}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Model</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedAsset.modelName}
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Status</Label>
                  <div className="text-sm p-2 bg-muted rounded flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${statusColors[selectedAsset.status]}`} />
                    <span className="capitalize">{selectedAsset.status}</span>
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Condition</Label>
                  <div className="text-sm p-2 bg-muted rounded flex items-center gap-2">
                    <div className={`w-2 h-2 rounded-full ${conditionColors[selectedAsset.condition]}`} />
                    <span className="capitalize">{selectedAsset.condition}</span>
                  </div>
                </div>
              </div>

              <div className="space-y-2">
                <Label className="text-sm font-medium">Location</Label>
                <div className="text-sm p-2 bg-muted rounded">
                  {getLocationName(selectedAsset.locationId)}
                </div>
              </div>

              <div className="space-y-2">
                <Label className="text-sm font-medium">Assigned To</Label>
                <div className="text-sm p-2 bg-muted rounded">
                  {getEmployeeName(selectedAsset.currentUserId)}
                </div>
              </div>

              {selectedAsset.serviceTag && (
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Service Tag</Label>
                  <div className="text-sm p-2 bg-muted rounded font-mono">
                    {selectedAsset.serviceTag}
                  </div>
                </div>
              )}

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Purchase Date</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedAsset.purchaseDate || "Not specified"}
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium">Warranty Expiry</Label>
                  <div className="text-sm p-2 bg-muted rounded">
                    {selectedAsset.warrantyExpiry || "Not specified"}
                  </div>
                </div>
              </div>
            </div>
          )}
          <DialogFooter>
            <Button variant="outline" onClick={() => setIsViewDialogOpen(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Asset Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit Asset</DialogTitle>
            <DialogDescription>
              Update information for {selectedAsset?.assetId}
            </DialogDescription>
          </DialogHeader>
          {selectedAsset && (
            <form onSubmit={handleUpdateAsset} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-assetType">Asset Type *</Label>
                  <Select name="assetType" defaultValue={selectedAsset.assetType} required>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="Laptop">Laptop</SelectItem>
                      <SelectItem value="Desktop">Desktop</SelectItem>
                      <SelectItem value="Monitor">Monitor</SelectItem>
                      <SelectItem value="Mobile">Mobile</SelectItem>
                      <SelectItem value="Tablet">Tablet</SelectItem>
                      <SelectItem value="Router">Router</SelectItem>
                      <SelectItem value="Switch">Switch</SelectItem>
                      <SelectItem value="Printer">Printer</SelectItem>
                      <SelectItem value="UPS">UPS</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-brand">Brand *</Label>
                  <Input
                    id="edit-brand"
                    name="brand"
                    defaultValue={selectedAsset.brand}
                    required
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-modelName">Model Name *</Label>
                  <Input
                    id="edit-modelName"
                    name="modelName"
                    defaultValue={selectedAsset.modelName}
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-serviceTag">Service Tag</Label>
                  <Input
                    id="edit-serviceTag"
                    name="serviceTag"
                    defaultValue={selectedAsset.serviceTag || ""}
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-locationId">Location</Label>
                  <Select name="locationId" defaultValue={selectedAsset.locationId?.toString() || ""}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select location" />
                    </SelectTrigger>
                    <SelectContent>
                      {locations?.map(location => (
                        <SelectItem key={location.id} value={location.id.toString()}>
                          {location.outletName}, {location.city}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-purchaseDate">Purchase Date</Label>
                  <Input
                    id="edit-purchaseDate"
                    name="purchaseDate"
                    type="date"
                    defaultValue={selectedAsset.purchaseDate || ""}
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="edit-warrantyExpiry">Warranty Expiry</Label>
                  <Input
                    id="edit-warrantyExpiry"
                    name="warrantyExpiry"
                    type="date"
                    defaultValue={selectedAsset.warrantyExpiry || ""}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-status">Status</Label>
                  <Select name="status" defaultValue={selectedAsset.status}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="available">Available</SelectItem>
                      <SelectItem value="assigned">Assigned</SelectItem>
                      <SelectItem value="maintenance">Maintenance</SelectItem>
                      <SelectItem value="retired">Retired</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="edit-condition">Condition</Label>
                <Select name="condition" defaultValue={selectedAsset.condition}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="excellent">Excellent</SelectItem>
                    <SelectItem value="good">Good</SelectItem>
                    <SelectItem value="fair">Fair</SelectItem>
                    <SelectItem value="poor">Poor</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <DialogFooter>
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsEditDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button type="submit" disabled={updateAssetMutation.isPending}>
                  {updateAssetMutation.isPending ? "Updating..." : "Update Asset"}
                </Button>
              </DialogFooter>
            </form>
          )}
        </DialogContent>
      </Dialog>
    </div>
  )
}

